package demo1;

public class oddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	       System.out.println("The Odd Numbers are:");
	        for (int i = 1; i <= 50; i++) {
	            if (i % 2 != 0) {
	                System.out.print(i + " ");
	            }
	        }
	    }
}
	    
	


