package variables;

public class oddorevennumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int x = 50;



         if(x%2 == 0)
         {
             System.out.println("The given number "+x+" is Even ");   

         }
         else
         {
             System.out.println("The given number "+x+" is odd ");  
	}
	}
}
